const nodemailer = require('nodemailer');

const USER_EMAIL = "dnsmnv06@mail.ru";
const USER_PASS = "0w5nUc5FiirTiJ1u3yiu";
const RECEIVER_EMAIL = "daniilsemenov435687@gmail.com";

const transporter = nodemailer.createTransport({
            host: 'smtp.mail.ru',
                port: 465,
                secure: true,
                auth: {
                    user: USER_EMAIL,  
                    pass: USER_PASS  
                },
            });


function send(message) {
       const options = {
                from: USER_EMAIL,           
                to: RECEIVER_EMAIL, 
                subject: "test",
                text: message,
                html: '<p>' + message + '</p>'
            };
        transporter.sendMail(options, (err, info) => {
           if (err) {
               console.log(err);
           } else {
               console.log(info);
           }
        });

}

module.exports = { send };